<script>
import Vue from "vue";
import {
  Tab,
  Tabs,
  Popup,
  Grid,
  GridItem,
  Stepper,
  DatetimePicker
} from "vant";
import { get } from "utils/http.js";
Vue.use(Tab)
  .use(Tabs)
  .use(Popup)
  .use(Grid)
  .use(GridItem)
  .use(Stepper)
  .use(DatetimePicker);
export default {
  data() {
    return {
      list: [],
      list2: [],
      num1: 0,
      num2: 0,
      value: ["2"],
      value1: ["0"],
      money: ["选择人均预算"],
      active: 2,
      show1: false,
      site: ["目的地"],
      MuLangue: 0,
      show2: false,
      show3: false,
      show4: false,
      minHour: 10,
      maxHour: 20,
      minDate: new Date(),
      maxDate: new Date(2019, 10, 1),
      currentDate: new Date(),
      time: ["选择出行时间"]
    };
  },
  methods: {
    //   出行时间方法
    showPopup1() {
      this.show1 = true;
    },
    //   出行时间方法
    showPopup2() {
      this.show2 = true;
    },
    //   出行时间方法
    showPopup3() {
      this.show3 = true;
    },
    showPopup4() {
      this.show4 = true;
    },
    setSite(Event) {
      this.site.push(Event.toElement.innerText);
      this.MuLangue = this.site.length - 1;
      this.show2 = false;
    },
    onChange(value) {
      this.num1 = value;
    },
    onChange1(value) {
      this.num2 = value;
    },
    handleClick() {
      this.value = [];
      this.value1 = [];
      this.value.splice(0, 1, this.num1);
      this.value1.push(this.num2);
      this.show4 = false;
    },
    handPick(value) {
      this.show3 = false;
      this.money.splice(0, 1, value);
    },
    handTimer(value) {
      var date = this.currentDate;
      var seperator1 = "-";
      var seperator2 = ":";
      var month = date.getMonth() + 1;
      var strDate = date.getDate();
      if (month >= 1 && month <= 9) {
        month = "0" + month;
      }
      if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
      }
      this.currentdate =
        date.getFullYear() + seperator1 + month + seperator1 + strDate;
      this.time.splice(0, 1, this.currentdate);
      this.show1 = false;
    },
    handClose() {
      this.show1 = false;
    }
  },
  async mounted() {
    let result = await get({
      url: "/api/v3/m1/customize/budget?platform=4"
    });
    this.list = result.data.list;
    let result1 = await get({
      url: "/api/v3/m1/area/group?scene=3&platform=4"
    });
    this.list2 = result1.data.list;
  }
};
</script>

